import java.util.*;
public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner s=new Scanner(System.in);
		int h=s.nextInt();
		int a=0;
		int b=0;
		for(int i=0;i<h;i++) {
			a=s.nextInt();
			b=s.nextInt();
			System.out.println(a+b);
		}

	}

}